$regex = "^[ *]*([0-9]+)-$short$"; 
$nums = @();
# Local branches
$local = git branch --list | ForEach-Object { $_.Trim() } | Where-Object { $_ -match $regex } | ForEach-Object { [int]($Matches[1]) };
$nums += $local;
# Remote heads
$remote = git ls-remote --heads origin | ForEach-Object { ($_ -split "\s+")[-1] } | Where-Object { $_ -match "refs/heads/([0-9]+)-$short$" } | ForEach-Object { [int]($Matches[1]) };
$nums += $remote;
# Specs directories
if (Test-Path specs) {
  $specdirs = Get-ChildItem specs -Directory | Select-Object -ExpandProperty Name | Where-Object { $_ -match "^([0-9]+)-$short$" } | ForEach-Object { [int]($Matches[1]) };
  $nums += $specdirs;
}
if ($nums.Count -eq 0) { $next = 1 } else { $next = ([int]($nums | Measure-Object -Maximum).Maximum) + 1 }
